const initialState = {
  good: 0,
  ok: 0,
  bad: 0,
};

const counterReducer = (state = initialState, action) => {
  console.log(action);
  switch (action.type) {
    case 'GOOD':
      state = { ...state, good: state.good + 1 };
      return state;
    case 'OK':
      return { ...state, ok: state.good + 1 };
    case 'BAD':
      state = { ...state, bad: state.good + 1 };
    case 'ZERO':
      return state;
    default:
      return state;
  }
};

export default counterReducer;
